package oo.mid;

public class Ticket {
	// TODO Auto-generated method stub
	int id;
	String name;
	float price;

	public void setId(int id) {
		this.id = id;
	}

	public int getId() {
		return id;
	}

	public void setName(String name) {
		this.name = name;
	}

	public String getName() {
		return name;
	}

	public void setPrice(float price) {
		this.price = price;
	}

	public Float getPrice() {
		return price;
	}
}
